import useTranslation from '@/hooks/useTranslation';
import { useAppDispatch } from '@/redux/hooks';
import { showModal } from '@/redux/modalSlice';
import { useEffect, useRef, useState } from 'react';
import Button from './Button';
import Logo from './Logo';
import MenuIcon from './MenuIcon';

export default function Footer() {
  const t = useTranslation();
  const footerRef = useRef<HTMLElement>(null);
  const [isMenuVisible, setIsMenuVisible] = useState(false);
  const dispatch = useAppDispatch();

  const handleScroll = () => {
    const el = footerRef.current;
    if (el?.getBoundingClientRect()) {
      if (el?.getBoundingClientRect()?.top >= window.innerHeight) {
        setIsMenuVisible(true);
      } else {
        setIsMenuVisible(false);
      }
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);
  return (
    <footer className="footer" ref={footerRef}>
      <div className="container">
        <div className="footer__inner">
          {isMenuVisible && (
            <Button className="mobile-menu" onClick={() => dispatch(showModal({ isModal: true }))}>
              <MenuIcon />
              <span>{t('Bo‘limlar')}</span>
            </Button>
          )}
          <div className="footer__logo__wrapper">
            <Logo className="footer__logo" />
          </div>
          <p className="footer__text">
            {t('"Bobotog‘ tongi" gazetasi barcha huquqlar himoyalangan.')}
          </p>
          <div className="footer__social">
            <a
              href="tel:+998975345141"
              target="_blank"
              className="footer__social__link"
              rel="noreferrer"
            >
              {socialIcon('phone')}
            </a>
            <a
              href="https://t.me/bobotog_tongi"
              target="_blank"
              className="footer__social__link"
              rel="noreferrer"
            >
              {socialIcon('mail')}
            </a>
            <a
              href="https://t.me/bobotog_tongi"
              target="_blank"
              className="footer__social__link"
              rel="noreferrer"
            >
              {socialIcon('telegram')}
            </a>
            <a href="/" target="_blank" className="footer__social__link">
              {socialIcon('facebook')}
            </a>
          </div>
          <a
            href="https://www.uzunsoft.uz/"
            target="_blank"
            className="footer__author"
            rel="noreferrer"
          >
            <p className="footer__text">{t('Ishlab chiqaruvchi:')}</p>
            {authorLogo()}
          </a>
        </div>
      </div>
    </footer>
  );
}

